import { Component } from '@angular/core';
import {Product, products} from "../products";

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent {
  products = products;

  share(product: Product) {

    const message = encodeURIComponent(`I'm sharing with you ${product.name}\n\n ${product.link}`);
    const telegramUrl = (`https://t.me/share/url?url=${message}`);
    window.open(telegramUrl);
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale')
  }
}

<div *ngFor="let product of products">

  <h3>
    <a href="{{ product.link }}">
      {{ product.name }}
    </a>
  </h3>

  <p class="price item"*ngIf="product.price">
    Price: {{ product.price }}
<!--    Description: {{ product.description }}-->
  </p>

  <p class="price item"*ngIf="product.category">
    Category: {{ product.category }}
  </p>

  <p *ngIf="product.description">
    Description: {{ product.description }}
  </p>

  <p *ngIf="product.image">
      <img src="{{ product.image}}">
  </p>

  <p class="rating item" *ngIf="product.rating">
    Rating: {{ product.rating }}
  </p>
  <button type="button" (click)="share(product)">
    Share
  </button>
  <app-product-alerts
    [product]="product"
    (notify)="onNotify()">
  </app-product-alerts>
</div>
