import {Component, Input, OnInit} from '@angular/core';

import {Product, products as allProducts} from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit{

  @Input() category?: string;
  filtered?: Product[];

  ngOnInit(): void {
    this.filtered =  this.getProductByCategory();
  }
  ngOnChanges(): void {
   this.filtered = this.getProductByCategory(); // auto call by Angular when category's state is changed
  }
  getProductByCategory() {
    if(this.category === "All Categories")
      return allProducts;
    return  allProducts.filter((x) => x.category === this.category)
  }
  share(product: Product) {
    const message = encodeURIComponent(`I'm sharing with you ${product.name}\n\n ${product.link}`);
    const telegramUrl = (`https://t.me/share/url?url=${message}`);
    window.open(telegramUrl);
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale')
  }

}

<div class="container products" *ngIf="getProductByCategory.length==0">
  <div class="container product"*ngFor="let product of filtered">
    <div class="container image">
        <img src="{{product.image}}">
    </div>
    <div class="text">
      <h3>
        <a href="{{ product.link }}">
          {{ product.name }}
        </a>
      </h3>
      <p class="rating">
        Rating: {{product.rating}}
      </p>
      <p>
        Price:{{product.price}}
      </p>
    </div>
    <div class="buttons">
      <button type="button" (click)="share(product)">
        Share
      </button>
      <app-product-alerts
        [product]="product"
        (notify)="onNotify()">
      </app-product-alerts>
    </div>
  </div>
</div>

<div class="emptyCategoryMsg">
  <h1>No products found in this category</h1>
</div>
