import { Injectable } from '@angular/core';
import {Product} from "./products";

@Injectable({
  providedIn: 'root'
})
export class CartService {

  items: Product[] = [];

  addToCart(product: Product) {
    this.items.push(product);
  }

  getItems() {
    return this.items;
  }

  clearCart() {
    this.items = [];
    return this.items;
  }
  removeItem(id: number) {
    this.items = this.items.filter((x) => x.id!==id);
    this.items.indexOf()
  }

  constructor() { }
}
