can be found in the LICENSE file at https://angular.io/license
*/

<div class="container items" *ngFor="let product of products">
  <div class="item" *ngIf="product.category=='Clothing'">
  <h3>
    <a href="{{ product.link }}">
      {{ product.name }}
    </a>
  </h3>

  <p class="price item"*ngIf="product.price">
    Price: {{ product.price }}
  </p>

  <p class="price item"*ngIf="product.category">
    Category: {{ product.category }}
  </p>

  <p *ngIf="product.description">
    Description: {{ product.description }}
  </p>

  <p *ngIf="product.image">
    <img src="{{ product.image}}">
  </p>

  <p class="rating item" *ngIf="product.rating">
    Rating: {{ product.rating }}
  </p>


  <button type="button" (click)="share(product)">
    Share
  </button>
  <app-product-alerts>
    [product]="product"
    (notify)="onNotify()">
  </app-product-alerts>
  </div>
</div>
