import {Component, EventEmitter, Input, Output} from '@angular/core';
import {Product} from "../products";

@Component({
  selector: 'app-product-share',
  templateUrl: './product-share.component.html',
  styleUrls: ['./product-share.component.css']
})
export class ProductShareComponent {
  @Input() product! : Product;
  @Output() share = new EventEmitter();

  shareViaTelegram(){
    const productUrl = this.product.link;
    const message = encodeURIComponent(`I'm sharing with you : ${productUrl}`);
    window.open()
  }
}
<p>product-share works!</p>

<p *ngIf="product.link">
  <button type="button" (click)="share.emit()">
    Share
  </button>
</p>
